disp("Start laboratory: ")% First problem %x = [0, pi/2, pi, 3*pi/2, 2*pi];xValues = sin(x);xx = linspace(0,2*pi);figure(1)title("First problem: ")givenPoint = [pi/4];givenPointValue = [sin(pi/4)];naturalSpline = spline(x,xValues,xx);plot(x,xValues,'o',xx,naturalSpline)xlabel("Value of x")ylabel("Value of sin(x) and natural spline")legend("sin(x)","Spline")disp("Value of the function at pi/4: ")sin(pi/4)disp("Value of the cubic natural spline at pi/4: ")naturalSpline = spline(x,xValues,givenPoint);disp("Value of the cubic clamped spline: ")spline(x, [1 xValues 1], pi/4)% Second problem %figure(2)title("Second problem: ")[xPoint, yPoint]= ginput(5);interval = linspace(min(xPoint),max(xPoint));resultingSpline = spline(xPoint,yPoint,interval);plot(xPoint, yPoint, "o",interval,resultingSpline)legend("points", "spline")